mapper.select:
select * from (select a.*, rownum rn from ( select ot.o_no as o_no, ot.m_email as m_email, d.d_address as d_address, d.d_detail_address as d_detail_address, d.d_name as d_name, ot.d_msg as d_msg, ot.o_total_price as o_total_price, ot.o_sale_price as o_sale_price, ot.o_pay_price as o_pay_price, ot.o_pay_type as o_pay_type, ot.o_date as o_date, ot.s_no as s_no
from order_tab ot left join DELIVERY d on ot.d_no = d.d_no where s_no=#{s_no} order by ot.o_no desc ) a )
where rn between #{startRow} and #{endRow}

select count(*) from order_tab ot
left join DELIVERY d on ot.d_no = d.d_no
where s_no=#{s_no}
