mapper.select:
select * from (select a.*,rowNum rn from ( select Q.qna_no as qna_no, Q.p_no as p_no, Q.op_no as op_no, Q.m_email as m_email, Q.qna_title as qna_title, Q.qna_question as qna_question, Q.qna_answer as qna_answer, Q.qna_ref as qna_ref, Q.qna_secret as qna_secret, Q.s_no as s_no, Q.qna_writedate as qna_writedate, Q.qna_delstatus as qna_delstatus, M.m_name as m_name, P.p_name as p_name, OP.o_no as o_no from qna Q left join member M on M.m_email = Q.m_email left join product P on P.p_no = Q.p_no left join order_product OP on OP.op_no = Q.op_no where qna_delstatus='N'
and Q.p_no=#{keyword} and Q.s_no=#{keyword} and Q.m_email=#{keyword}
order by qna_writedate desc ) a) where rn between #{startRow} and #{endRow}

 		select count(*) from qna  where qna_delstatus='N'
 	    
	         and p_no=#{keyword} 
	         and s_no=#{keyword} 
	         and m_email=#{keyword}
